package firstPacakage;

public class Operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Unary Operator
		int a=1;
		System.out.println(a);// 1
		System.out.println(a++); //1 and a value is increased after printing
		System.out.println(a);// 2
		System.out.println(++a);// before printing value  is increased to 3
		
		System.out.println(a   ++); //3 and a value is increased after printing
		System.out.println(a);//4
		System.out.println(++   a);//5
	}

}
