package firstPacakage;

public class WhileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10;
		while(i<20 & i<12) {
			System.out.println(" value of "+i);
			i++;
		}
		System.out.println("i");
		
		int a=5;
		while(a>0) {
			System.out.println(a);
			a--;
		}
	}

}
